import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './LoginStyle.css';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const handleLogin = async () => {
    if (!email || !password) {
      setMessage("Email dan password harus diisi");
      return;
    }
  
    try {
      const response = await fetch('http://localhost:8080/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password, rememberMe }),
      });
  
      const data = await response.json();
  
      if (response.ok) {
        setMessage(`Login berhasil. Selamat datang, ${data.user?.name || 'User'}`);
        localStorage.setItem('token', data.token); 
      } else {
        setMessage(data.error || "Login gagal");
      }
    } catch (err) {
      setMessage("Terjadi kesalahan saat menghubungi server");
      console.error("Login error:", err);
    }
  };

  const goToRegister = () => {
    window.location.href = "/register";
  };

  return (
    <div class="login-page">
      <header class="header"></header>
      <div class="header-image">
        <img src="/images/grownncheer_logo.png"/>
      </div>
      <div class="login-container">
        <h2>Login</h2>
        <input
          type="email"
          placeholder="Email"
          class="input-field"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <div class="password-wrapper">
          <input
            type={showPassword ? 'text' : 'password'}
            placeholder="Password"
            class="input-field"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button
            type="button"
            class="toggle-password"
            onClick={() => setShowPassword(!showPassword)}
          >
            {showPassword ? '🙈' : '👁️'}
          </button>
        </div>

        <div class="remember-me">
          <div>
            <input
              type="checkbox"
              id="remember"
              checked={rememberMe}
              onChange={() => setRememberMe(!rememberMe)}
            />
            <label htmlFor="remember">Ingat saya</label>
          </div>
          <span class="forgot-pass" onClick={goToRegister}>Lupa password?</span>
        </div>

        <button class="login-button" onClick={handleLogin}>Login</button>
        <p class="message">{message}</p>
        <p class="register-text">
          Belum punya akun? <Link className="register-link" to="/register">Daftar disini</Link>
        </p>
      </div>
    </div>
  );
}

export default LoginPage;
