import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // ✅ hanya 1 kali import
import './LoginStyle.css';
import api from './api';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate(); // ✅ buat redirect

  const handleLogin = async () => {
    if (!email || !password) {
      setMessage("Email dan password harus diisi");
      return;
    }

    try {
      const response = await api.post('/auth/login', {
        email,
        password,
        rememberMe
      });

      const data = response.data;

      if (data.status === 'success') {
        setMessage(`Login berhasil. Selamat datang, ${data.data?.name || 'User'}`);
        localStorage.setItem('token', data.token || ''); // ✅ token
        localStorage.setItem('user', JSON.stringify(data.data || {})); // ✅ simpan user info
        navigate('/profile'); // ✅ redirect ke /profile
      } else {
        setMessage(data.message || "Login gagal");
      }

    } catch (err) {
      if (err.response) {
        const { status, data } = err.response;
        if (data.status === 'wrong_credentials') {
          setMessage("Email atau password salah");
        } else if (data.status === 'invalid_input') {
          setMessage(data.message || "Input tidak valid");
        } else {
          setMessage(data.message || "Terjadi kesalahan di server");
        }
      } else {
        setMessage("Gagal menghubungi server");
      }

      console.error("Login error:", err);
    }
  };

  return (
    <div className="login-page">
      <header className="login-header"></header>
      <div className="login-header-image">
        <img src="/images/grownncheer_logo.png" alt="Logo" />
      </div>
      <div className="login-container">
        <h2>Login</h2>
        <input
          type="email"
          placeholder="Email"
          className="login-input-field"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <div className="login-password-wrapper">
          <input
            type={showPassword ? 'text' : 'password'}
            placeholder="Password"
            className="login-input-field"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button
            type="button"
            className="login-toggle-password"
            onClick={() => setShowPassword(!showPassword)}
          >
            {showPassword ? '🙈' : '👁️'}
          </button>
        </div>

        <div className="login-remember-me">
          <input
            type="checkbox"
            id="remember"
            checked={rememberMe}
            onChange={() => setRememberMe(!rememberMe)}
          />
          <label htmlFor="remember">Ingat saya</label>
        </div>

        <button className="login-button" onClick={handleLogin}>Login</button>
        <p className="login-message">{message}</p>
        <p className="login-register-text">
          Belum punya akun? <Link className="login-register-link" to="/register">Daftar disini</Link>
        </p>
      </div>
    </div>
  );
}

export default LoginPage;
