import React, { useState } from 'react';
import './LoginStyle.css';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [rememberMe, setRememberMe] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const handleLogin = async () => {
    if (!email || !password) {
      const emptyResponse = await fetch('/response_empty_bf.json');
      const data = await emptyResponse.json();
      setMessage(data.error);
      return;
    }

    try {
      const response = await fetch('http://localhost:3306/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password, rememberMe })
      });

      const data = await response.json();

      if (data.message === "Login sukses") {
        setMessage(`Login berhasil. Selamat datang, ${data.user.name}`);
        localStorage.setItem('token', data.token);
      } else if (data.error) {
        setMessage(data.error);
      }
    } catch (err) {
      setMessage("Terjadi kesalahan saat menghubungi server");
    }
  };

  const goToRegister = () => {
    window.location.href = "/register";
  };

  return (
    <div className="background">
      <div className="login-page">
        {/* Header abu-abu dengan panjang 5px */}
        <header className="header"></header>

        {/* Gambar di atas login container */}
        <div className="header-image">
          <img src="/images/grownncheer_logo.png"/>
        </div>

        <div className="login-container">
          <h2>Login</h2>
          <input
            type="email"
            placeholder="Email"
            className="input-field"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <div className="password-wrapper">
            <input
              type={showPassword ? 'text' : 'password'}
              placeholder="Password"
              className="input-field"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button
              type="button"
              className="toggle-password"
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? 'üôà' : 'üëÅÔ∏è'}
            </button>
          </div>

          <div className="remember-me">
            <div>
              <input
                type="checkbox"
                id="remember"
                checked={rememberMe}
                onChange={() => setRememberMe(!rememberMe)}
              />
              <label htmlFor="remember">Ingat saya</label>
            </div>
            <span className="forgot-pass" onClick={goToRegister}>Lupa password?</span>
          </div>

          <button className="login-button" onClick={handleLogin}>Login</button>
          <p className="message">{message}</p>
          <p className="register-text">
            Belum punya akun? <span className="register-link" onClick={goToRegister}>Daftar disini</span>
          </p>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
