import React, { useState } from 'react';
import './LoginStyle.css';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async () => {
    if (!email || !password) {
      // Menangani kasus ketika email atau password kosong
      const emptyResponse = await fetch('/response_empty_bf.json');
      const data = await emptyResponse.json();
      setMessage(data.error);
      return;
    }

    try {
      // Membaca data request dari request_fb.json
      const requestPayload = await fetch('/request_fb.json').then(res => res.json());

      // Membandingkan input dengan data request_fb.json
      if (email !== requestPayload.email || password !== requestPayload.password) {
        const wrongResponse = await fetch('/response_wrong_bf.json');
        const wrongData = await wrongResponse.json();
        setMessage(wrongData.error);
        return;
      }

      // Jika email dan password cocok, simulasikan response sukses
      const successResponse = await fetch('/response_success_bf.json');
      const successData = await successResponse.json();

      if (successData.message === "Login sukses") {
        setMessage(`Login berhasil. Selamat datang, ${successData.user.name}`);
        // Kamu bisa menyimpan token JWT di sini jika perlu
        // localStorage.setItem('token', successData.token);
      }

    } catch (err) {
      setMessage("Terjadi kesalahan saat menghubungi server");
    }
  };

  const goToRegister = () => {
    window.location.href = "/register";
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <input
        type="email"
        placeholder="Email"
        className="input-field"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        className="input-field"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button className="login-button" onClick={handleLogin}>Login</button>
      <button className="register-button" onClick={goToRegister}>Ke Registrasi</button>
      <p className="message">{message}</p>
    </div>
  );
}

export default LoginPage;
