import React, { useState } from 'react';
import './LoginStyle.css';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async () => {
    if (!email || !password) {
      const emptyResponse = await fetch('/response_empty_bf.json');
      const data = await emptyResponse.json();
      setMessage(data.error);
      return;
    }
  
    try {
      const response = await fetch('http://localhost:5000/login', { // URL API Backend
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
  
      const data = await response.json();
  
      if (data.message === "Login sukses") {
        setMessage(`Login berhasil. Selamat datang, ${data.user.name}`);
        // Simpan token jika diperlukan
        localStorage.setItem('token', data.token);
      } else if (data.error) {
        setMessage(data.error);
      }
    } catch (err) {
      setMessage("Terjadi kesalahan saat menghubungi server");
    }
  };  

  const goToRegister = () => {
    window.location.href = "/register";
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <input
        type="email"
        placeholder="Email"
        className="input-field"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        className="input-field"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button className="login-button" onClick={handleLogin}>Login</button>
      <button className="register-button" onClick={goToRegister}>Ke Registrasi</button>
      <p className="message">{message}</p>
    </div>
  );
}

export default LoginPage;
